const base64String =""
const pdfjsLib = window['pdfjs-dist/build/pdf']
pdfjsLib.GlobalWorkerOptions.workerSrc = '//mozilla.github.io/pdf.js/build/pdf.worker.js'

const searchText = document.querySelector('#search-text')
const displayText = document.querySelector('#display-text')
const loadingTask = pdfjsLib.getDocument({data: atob(base64String) })


const downloadFile = async () => {

fetch('https://search-project-domain-wpdtxrrvxuzxyvbm7ya4qv3mwu.us-east-1.es.amazonaws.com/docs6/_search?pretty=true&q=bla2')
  .then(response => response.json())
  .then(data => alert(data.hits.hits[0]._source.textDoc))
  .catch(error => console.error(error));

    
    const pdf = await loadingTask.promise;
    const numPages = await pdf.numPages;
    const pageTextPromises = [];

    for (let i = 1; i <= numPages; i++) {
      const page = pdf.getPage(i);
      const pageTextPromise = page.then(page => page.getTextContent());
      pageTextPromises.push(pageTextPromise);
    }
    
    //get matadata (date create file)
    const metadata = await pdf.getMetadata()
    const creationDate = metadata.info.CreationDate;
    const year = creationDate.substring(2, 6);
    const month = parseInt(creationDate.substring(6, 8)) - 1;
    const day = creationDate.substring(8, 10);
    const dateCreate = `${day}-${month}-${year}`

    Promise.all(pageTextPromises,metadata).then(pageTexts => {
      const fileText =  pageTexts.map(pageText => pageText.items.map(item => item.str));
      const allFileText = fileText.join('\n');
      const fileName = fileText[0][0]

      const data = {
        "data": 
        [
          {
            allFileText, 
            fileName,
            numPages,
            dateCreate
          }
        ]
      }

      // const data = { 
      //   fullText, 
      //   fileName,
      // };
      
      const file = new File([JSON.stringify(data, null, 2)], fileName, { type: "application/json" });
     
      //make json file and download
      const link = document.createElement("a");
      link.href = URL.createObjectURL(file);
      link.download = fileName;
      link.click();
    });
}


searchText.addEventListener('input', async(e) => {

  const wordToSearch = e.target.value.toLowerCase();
  const displayText = document.getElementById("display-text");
  displayText.innerHTML = '';
  
  const pdf = await loadingTask.promise

  for(let i = 1; i <= await pdf.numPages; i++) {
    const page = await pdf.getPage(i)
    const textContent = await page.getTextContent()
    const pageText = textContent.items.map(item => item.str).join('').toLowerCase();
    // console.log(`Page ${i}: ${pageText}`);

    const findWord = findWordContainingSubstring(pageText, wordToSearch)

    if (findWord != null) { //find..
      const result = getWordsAroundSearchTerm(pageText, findWord);
      let findText = `${result.replace(new RegExp(wordToSearch, 'gi'), '<b>$&</b>')}`

      const resultElement = document.createElement("div");
      resultElement.innerHTML = findText
      
      displayText.appendChild(resultElement);
    }     
  }
})


function findWordContainingSubstring(str, substr) {
  const regex = new RegExp(`\\b\\w*${substr}\\w*\\b`, 'i');
  const match = str.match(regex);
  return match ? match[0] : null;
}

function getWordsAroundSearchTerm(str, searchTerm, wordsBefore = 9, wordsAfter = 9) {
  const words = str.split(' ');
  const index = words.findIndex(word => word.includes(searchTerm));

  if (index === -1)  return [];

  const startIndex = Math.max(0, index - wordsBefore);
  const endIndex = Math.min(words.length, index + wordsAfter + 1);
  const result = words.slice(startIndex, endIndex);

  return result.join(' ');
}
